package test;

import org.apache.commons.io.IOUtils;

import java.io.File;
import java.io.FileOutputStream;
import java.io.OutputStream;
import java.util.Base64;

/**
 * @description: base64测试
 * @author: huangCW
 * @create: 2020-12-03 19:18
 */
public class Base64Test {

    public static void main(String[] args){
        String base64Str = "";

        try{
            byte[] buffer = Base64.getDecoder().decode(base64Str);
            File file = new File("E:\\collection_util\\src\\main\\java\\file\\test.xlsx");
            OutputStream outputStream = new FileOutputStream(file);
            IOUtils.write(buffer,outputStream);
            outputStream.close();
        }catch (Exception e){
            System.out.println("解码错误");
        }

    }
}
